(1784, 10, 'texte_1', 'Offrez-vous une étude complète d’architecte d’intérieur dès lors que vous êtes intéressés par du mobilier de notre magasin.\r\n\r\n<strong>Pour 150 € au lieu de 1100 €, vous recevez un projet de réaménagement complet d’une pièce ou de l’ensemble de votre maison.</strong>'),
(1785, 10, '_texte_1', 'field_5e27211381a19'),
(1786, 10, 'repeater_1_0_sub_text', 'test A'),
(1787, 10, '_repeater_1_0_sub_text', 'field_5e27215381a1b'),
