(1810, 174, 'texte_1', 'Offrez-vous une étude complète d’architecte d’intérieur dès lors que vous êtes intéressés par du mobilier de notre magasin.\r\n\r\n<strong>Pour 150 € au lieu de 1100 €, vous recevez un projet de réaménagement complet d’une pièce ou de l’ensemble de votre maison.</strong>'),
(1811, 174, '_texte_1', 'field_5e27211381a19'),
(1812, 174, 'repeater_1_0_sub_text', ''),
(1813, 174, '_repeater_1_0_sub_text', 'field_5e27215381a1b'),
